import React, {useState} from 'react';

function IngredientsInput(props) {
    const ingredientSchema = {
        ingredientId: undefined,
        ingredientName: "",
        ingredientAmount: undefined,
        ingredientUnit: ""
    };

    const ingredients = props.inputs.recipieIngredients;

    function addIngredientToList() {
        props.setInputs(values => ({...values, recipieIngredients: [...values.recipieIngredients, ]}));
    }
    
    function deleteIngredientFromList(id) {
        props.setInputs((prevValue) => {
            return prevValue.filter((value, index) => {
            return index !== id;
            });
        });
    }

    return(
        <div className="mb-3">
            {props}
        </div>
    );
}

export default IngredientsInput;
