    const { recipieId } = useParams();

    const currentRecipie = recipies.filter(recipie => {
        console.log(recipie.id);
        return recipie.id == recipieId;
    })
    console.log(currentRecipie);

    const Steps = currentRecipie[0].Steps;

    return (
        <div>
        <h1>Dummy</h1>
        <h1>{currentRecipie[0].title} </h1>
        </div>
    )
}

export default RecipiePage;
