import React from 'react';
import { Card, Button } from 'react-bootstrap';

function RecipieCard(props) {
    return(
        <Card style={{ width: '18rem' ,height: '24rem', margin:18}} onClick={() => {props.changeClicked}}>
            <div className='imageDiv'>
                <Card.Img variant="top" src={props.imgSource} style={{ height:"100%", width: "100%", objectFit: "contain"}} />
            </div>
            <Card.Body>
                <Card.Title>{props.title}</Card.Title>
                <Card.Text>{props.prevText}
                </Card.Text>
            </Card.Body>
        </Card>
    );
}

export default RecipieCard;
