import React, {useState} from 'react';

function AddNewRecipie() {
    const ingredientSchema = {
        ingredientId: Number,
        ingredientName: String,
        ingredientAmount: Number,
        ingredientUnit: String
    };

    const imageSchema = {
        imageName: String,
        path: String,
        size: Number
    };

    const stepSchema = {
        stepId: Number,
        stepTitle: String,
        stepDescription: String,
        stepImages: [imageSchema]
    };

    const [inputs, setInputs] = useState({
        recipieTitle: String,
        recipieIngredients: [ingredientSchema],
        recipieDescripton: String,
        recipieIsVeg: Boolean,
        recipiePrevImage: imageSchema,
        recipieCalories: Number,
        recipieTags: [String],
        recpiesteps: [stepSchema]
    });

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;

        if (name === 'recipieIsVeg') {
            if (value === 'veg') {
                setInputs(values => ({...values, [name]: true}))
            } else if (value === 'nonveg') {
                setInputs(values => ({...values, [name]: false}))
            }
        } else {
            setInputs(values => ({...values, [name]: value}))
        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
    }

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                name="recipieTitle"
                placeholder='Enter Recipie name' 
                value={inputs.recipieTitle} 
                onChange={handleChange}
            />
            <select name='recipieIsVeg' onChange={handleChange} value={inputs.recipieIsVeg}>
                <option value="veg">Veg</option>
                <option value="nonveg">non-Veg</option>
            </select>
            <textarea 
                type="text" 
                name="recipieDescripton"
                placeholder="Enter Recipie description"
                value={inputs.recipieDescripton} 
                onChange={handleChange}
            />
            <input type="submit" />
        </form>
    );
}

export default AddNewRecipie;
