import React, {useState} from 'react';

function AddNewRecipie() {
    const ingredientSchema = {
        ingredientId: 0,
        ingredientName: "",
        ingredientAmount: 0,
        ingredientUnit: ""
    };

    const imageSchema = {
        imageName: "",
        path: "",
        size: 0
    };

    const stepSchema = {
        stepId: 0,
        stepTitle: "",
        stepDescription: "",
        stepImages: []
    };

    const [isKeyReleased, setIsKeyReleased] = useState(false);

    const [tagInput, setTagInput] = useState('');

    const [inputs, setInputs] = useState({
        recipieTitle: "",
        recipieIngredients: [],
        recipieDescripton: "",
        recipieIsVeg: "",
        recipiePrevImage: {},
        recipieCalories: 0,
        recipieTags: [],
        recpiesteps: []
    });

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;

        if (name === "tag") {
            setTagInput(value);
        } else {
            setInputs(values => ({...values, [name]: value}));
        }
    }

    const onKeyDown = (e) => {
        const { key } = e;
        const trimmedInput = input.trim();
      
        if (key === ',' && trimmedInput.length && !tags.includes(trimmedInput)) {
          e.preventDefault();
          setTags(prevState => [...prevState, trimmedInput]);
          setInput('');
        }
      
        if (key === "Backspace" && !input.length && tags.length && isKeyReleased) {
          const tagsCopy = [...tags];
          const poppedTag = tagsCopy.pop();
          e.preventDefault();
          setTags(tagsCopy);
          setInput(poppedTag);
        }
      
        setIsKeyReleased(false);
      };
      
      const onKeyUp = () => {
        setIsKeyReleased(true);
      }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
    }

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                name="recipieTitle"
                placeholder='Enter Recipie name' 
                value={inputs.recipieTitle} 
                onChange={handleChange}
            />
            <input 
                type="number" 
                name="recipieCalories"
                placeholder='Enter Recipie calories' 
                value={inputs.recipieCalories} 
                onChange={handleChange}
            />
            <select name='recipieIsVeg' onChange={handleChange} value={inputs.recipieIsVeg}>
                <option value="veg">Veg</option>
                <option value="nonveg">non-Veg</option>
            </select>
            <textarea 
                type="text" 
                name="recipieDescripton"
                placeholder="Enter Recipie description"
                value={inputs.recipieDescripton} 
                onChange={handleChange}
            />
            <div className="container">
                {inputs.recipieTags.map((tag) => <div className="tag">{tag}</div>)}
                <input
                    name="tag"
                    value={tagInput}
                    placeholder="Enter a tag"
                    onKeyDown={onKeyDown} 
                    onKeyUp={onKeyUp} 
                    onChange={onChange}
                />
            </div>

            <input type="submit" />
        </form>
    );
}

export default AddNewRecipie;
