import React, {useState} from 'react';

function AddNewRecipie() {
    const ingredientSchema = {
        ingredientId: 0,
        ingredientName: "",
        ingredientAmount: 0,
        ingredientUnit: ""
    };

    const imageSchema = {
        imageName: "",
        path: "",
        size: 0
    };

    const stepSchema = {
        stepId: 0,
        stepTitle: "",
        stepDescription: "",
        stepImages: []
    };

    const [inputs, setInputs] = useState({
        recipieTitle: "",
        recipieIngredients: [],
        recipieDescripton: "",
        recipieIsVeg: "",
        recipiePrevImage: {},
        recipieCalories: 0,
        recipieTags: "",
        recpiesteps: []
    });

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;

        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
    }

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                name="recipieTitle"
                placeholder='Enter Recipie name' 
                value={inputs.recipieTitle} 
                onChange={handleChange}
            />
            <input 
                type="number" 
                name="recipieCalories"
                placeholder='Enter Recipie calories' 
                value={inputs.recipieCalories} 
                onChange={handleChange}
            />
            <select name='recipieIsVeg' onChange={handleChange} value={inputs.recipieIsVeg}>
                <option value="veg">Veg</option>
                <option value="nonveg">non-Veg</option>
            </select>
            <textarea 
                type="text" 
                name="recipieDescripton"
                placeholder="Enter Recipie description"
                value={inputs.recipieDescripton} 
                onChange={handleChange}
            />
            <textarea 
                type="text" 
                name="recipieTags"
                placeholder="Enter Recipie tags"
                value={inputs.recipieTags} 
                onChange={handleChange}
            />

            <input type="submit" />
        </form>
    );
}

export default AddNewRecipie;
