import React, {useState} from 'react';
import TagsInput from './TagsInput';


function AddNewRecipie() {
    const ingredientSchema = {
        ingredientId: 0,
        ingredientName: "",
        ingredientAmount: 0,
        ingredientUnit: ""
    };

    const imageSchema = {
        imageName: "",
        path: "",
        size: 0
    };

    const stepSchema = {
        stepId: 0,
        stepTitle: "",
        stepDescription: "",
        stepImages: []
    };

    const [inputs, setInputs] = useState({
        recipieTitle: "",
        recipieIngredients: [],
        recipieDescripton: "",
        recipieIsVeg: "",
        recipiePrevImage: {},
        recipieCalories: undefined,
        recpiesteps: []
    });

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;

        setInputs(values => ({...values, [name]: value}));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
    }

    return (
        <form onSubmit={handleSubmit}>
            <div class="form-group">
                <input 
                    className='form-control'
                    type="text" 
                    name="recipieTitle"
                    placeholder='Enter Recipie name' 
                    value={inputs.recipieTitle} 
                    onChange={handleChange}
                />
                <input 
                    className='form-control'
                    type="number" 
                    name="recipieCalories"
                    placeholder='Enter Recipie calories' 
                    value={inputs.recipieCalories} 
                    onChange={handleChange}
                />
            </div>
            <select name='recipieIsVeg' onChange={handleChange} value={inputs.recipieIsVeg}>
                <option value="veg">Veg</option>
                <option value="nonveg">non-Veg</option>
            </select>
            <textarea 
                type="text" 
                name="recipieDescripton"
                placeholder="Enter Recipie description"
                value={inputs.recipieDescripton} 
                onChange={handleChange}
            />
            <TagsInput />

            <input type="submit" />
        </form>
    );
}

export default AddNewRecipie;
