import React, {useEffect, useState} from 'react';
import RecipieCard from './RecipieCard';
import axios from "axios";

const client = axios.create({
  baseURL: "http://localhost:5000" 
});

const recipies = [{
    id: 1,
    title:"Milk Tea",
    prevText:"Ready in 10 mins!",
    imgSource:"https://www.teaforturmeric.com/wp-content/uploads/2021/11/Masala-Chai-Tea-9-728x1092.jpg",
    Steps: [{
        stepId:1,
        stepText: "Boil water"
    },{
        stepId:2,
        stepText:"add tea"
    }]
},
{
    id: 2,
    title:"Hakka Noodles",
    prevText:"Easy chinese in lesss than 10 mins!",
    imgSource:"https://shwetainthekitchen.com/wp-content/uploads/2020/07/IMG_0100.jpg",
    Steps: [{
        stepId: 1,
        stepText: "Boil water"
    },{
        stepId: Number(2),
        stepText:"add noodles"
    }]
}];

function RecipieList() {
    const [recipies1, setRecipies1] = useState([]);

    useEffect(() => {
        client.get('/').then((response) => {
            setRecipies1(response.data);
        });
     }, []);

    return (
        <div className='row'>
            {recipies1.map((currentRecipie, index) => {
                return (<RecipieCard id={index} key={index} title={currentRecipie.recpieTitle} prevText={currentRecipie.recipiePrevDescription} imgSource={currentRecipie.recipiePrevImage.path}  />)
            })}
        </div>
    )
}

export default RecipieList;
export {recipies};
