import React, {useState} from 'react';

function StepsInput(props) {
    const [step, setStep] = useState({
        stepId: 0,
        stepTitle: "",
        stepDescription: "",
        stepImages: [{
            imageName: "",
            path: "",
            size: undefined
        }]
    });

    const steps = props.inputs.recpiesteps;

    function deleteStepFromList(index) {
        props.setInputs(values => {
            return ({...values, recpiesteps: values.recpiesteps.filter((tag, i) => i !== index)});
        });
    }

    function handleChange(event) {
        const { name, value } = event.target;
    
        setStep((prevValue) => {
          return { ...prevValue, [name]: value };
        });
    }

    return (
        <div className="mb-3">
            <h4>Steps</h4>
            <hr></hr>
            <div className='mb-3'>
                {steps.map((currentStep, index) => (
                    <div key={index} className='mb-3 col-md-12'>
                        <h3>{currentStep.stepTitle}</h3> <hr></hr> {currentStep.stepDescription}
                        <button className="btn btn-outline-dark" onClick={() => deleteStepFromList(index)}>remove step</button>
                    </div>
                ))}
            </div>
            <div className='mb-3 row'>
                <div className ="mb-3 col-md-3">
                    <input
                        className ='form-control'
                        type="text"
                        onChange={handleChange}
                        name="stepTitle"
                        placeholder="Step Title"
                        value={step.stepTitle}
                    />
                </div>
                <div className ="mb-3 col-md-9">
                    <input
                        className ='form-control'
                        type="text"
                        onChange={handleChange}
                        name="stepTitle"
                        placeholder="Step Title"
                        value={step.stepTitle}
                    />
                </div>
            </div>    
        </div>
    );
}

export default StepsInput;
