import React, {useEffect, useState} from 'react';
import RecipeCard from './RecipeCard';
import axios from "axios";
import { useParams} from "react-router-dom";

const client = axios.create({
  baseURL: "http://localhost:5000" 
});


function RecipeList(props) {
    const [recipes1, setrecipes1] = useState(null);

    const { searchQuery } = useParams();

    const url = encodeURI('/' + props.type==="search" ? ("search/" + searchQuery): "");

    console.log(url);

    useEffect(() => {
        client.get(url).then((response) => {
            setrecipes1(response.data);
        });
    });

    if (recipes1 === null) {
        return (<h1>Loading...</h1>)
    } else {
        return (
            <div className='row'>
                {recipes1.map((currentrecipe, index) => {
                    return (<RecipeCard id={index} key={index} title={currentrecipe.recipeTitle} prevText={currentrecipe.recipeDescripton} imgSource={currentrecipe.recipePrevImage.path}  />)
                })}
            </div>
        )
    }
}

export default RecipeList;
