import React, {useState} from 'react';
import TagsInput from './TagsInput';
import IngredientsInput from './IngredientsInput';
import StepsInput from './StepsInput';
import axios from "axios";

function AddNewRecipie() {
    const imageSchema = {
        imageName: "",
        path: "",
        size: 0
    };

    const [inputs, setInputs] = useState({
        recipieTitle: "",
        recipieIngredients: [],
        recipieDescripton: "",
        recipieIsVeg: "",
        recipieTags: [],
        recipiePrevImage: {},
        recipieCalories: undefined,
        recpiesteps: []
    });

    // const [selectedFile, setSelectedFile] = useState(null);

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;

        if (name==='recipiePrevImage') {
            const file = event.target.files[0];
            setInputs(values => ({...values, recipiePrevImage: file}))
        } else {
            setInputs(values => ({...values, [name]: value}));
        }
    }


    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData();

        formData.append('recipieTitle',inputs.recipieTitle);
        formData.append('recipieIngredients',inputs.recipieIngredients);
        formData.append('recipieDescripton',inputs.recipieDescripton);
        formData.append('recipieIsVeg',inputs.recipieIsVeg);
        formData.append('recipieTags',inputs.recipieTags);
        formData.append('recipiePrevImage',inputs.recipiePrevImage);
        formData.append('recipieCalories',inputs.recipieCalories);
        formData.append('recpiesteps',inputs.recpiesteps);

        console.log(inputs);

        axios.post('http://localhost:5000/recipies/addNew',formData)
            .then(res => (console.log(res)))
            .catch(err => (console.log(err)));
    }

    return (
        <div>
        <h2>Enter details</h2>
        <hr></hr>
        <form onSubmit={handleSubmit}>
            <div className ="mb-3 row">
                <div className ="mY-3 col-md-6">   
                    <input 
                        className='form-control' 
                        type="text" 
                        name="recipieTitle"
                        placeholder='Enter Recipie name' 
                        value={inputs.recipieTitle} 
                        onChange={handleChange}
                    />
                </div>
                <div className ="mY-3 col-md-4">
                    <input 
                        className ='form-control'
                        type="number" 
                        name="recipieCalories"
                        placeholder='Enter Recipie calories' 
                        value={inputs.recipieCalories || ''} 
                        onChange={handleChange}
                    />
                </div>
                <div className ="mY-3 col-md-2">
                    <select className="form-control" name='recipieIsVeg' onChange={handleChange} value={inputs.recipieIsVeg}>
                        <option value="">--choose--</option>
                        <option value="veg">Veg</option>
                        <option value="nonveg">non-Veg</option>
                    </select>
                </div>
            </div>
            <div className="mb-3 col-md-12"> 
                <textarea 
                    className='form-control' 
                    type="text" 
                    name="recipieDescripton"
                    placeholder="Enter Recipie description"
                    value={inputs.recipieDescripton} 
                    onChange={handleChange}
                />
            </div>
            <div className='mb-3 col-md-3'>
                <label htmlFor='prevImage' >Upload preview image</label>
                <input onChange={handleChange} type="file" id="prevImage" className='form-control' name='recipiePrevImage' />
            </div>
            <TagsInput inputs={inputs} setInputs={setInputs} />
            <IngredientsInput inputs={inputs} setInputs={setInputs} />
            <StepsInput inputs={inputs} setInputs={setInputs} />
            <button type="submit" className="btn btn-outline-dark">submit</button>
        </form>
        </div>
    );
}

export default AddNewRecipie;
