import React, { useState } from 'react';
import RecipieCard from './RecipieCard';
import { useNavigate } from "react-router-dom";

const recipies = [{
    title:"Milk Tea",
    prevText:"Ready in 10 mins!",
    imgSource:"https://www.teaforturmeric.com/wp-content/uploads/2021/11/Masala-Chai-Tea-9-728x1092.jpg"
},
{
    title:"Hakka Noodles",
    prevText:"Easy chinese in lesss than 10 mins!",
    imgSource:"https://shwetainthekitchen.com/wp-content/uploads/2020/07/IMG_0100.jpg"
}];

function CentreDisplay() {
    const navigate = useNavigate();
    
    return (
        
        <div className='row'>
            {recipies.map((currentRecipie) => {
                return (<RecipieCard title={currentRecipie.title} prevText={currentRecipie.prevText} imgSource={currentRecipie.imgSource} onClick={() => {
                navigate(`/products/${product.id}`);
              }} />)
            })}
        </div>
    );
    
    
}

export default CentreDisplay;
