import React, {useState} from 'react';
import TagsInput from './TagsInput';


function AddNewRecipie() {
    const ingredientSchema = {
        ingredientId: 0,
        ingredientName: "",
        ingredientAmount: 0,
        ingredientUnit: ""
    };

    const imageSchema = {
        imageName: "",
        path: "",
        size: 0
    };

    const stepSchema = {
        stepId: 0,
        stepTitle: "",
        stepDescription: "",
        stepImages: []
    };

    const [inputs, setInputs] = useState({
        recipieTitle: "",
        recipieIngredients: [],
        recipieDescripton: "",
        recipieIsVeg: "",
        recipieTags: [],
        recipiePrevImage: {},
        recipieCalories: undefined,
        recpiesteps: []
    });

    const [selectedFile, setSelectedFile] = useState(null);

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;

        setInputs(values => ({...values, [name]: value}));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const file = event.target.files[0];

        console.log(file);
        console.log(inputs);
    }

    return (
        <div>
        <h2>Enter details</h2>
        <hr></hr>
        <form onSubmit={handleSubmit}>
            <div className ="mb-3 row">
                <div className ="mb-3 col-md-6">   
                    <input 
                        className='form-control' 
                        type="text" 
                        name="recipieTitle"
                        placeholder='Enter Recipie name' 
                        value={inputs.recipieTitle} 
                        onChange={handleChange}
                    />
                </div>
                <div className ="mb-3 col-md-4">
                    <input 
                        className ='form-control'
                        type="number" 
                        name="recipieCalories"
                        placeholder='Enter Recipie calories' 
                        value={inputs.recipieCalories || ''} 
                        onChange={handleChange}
                    />
                </div>
                <div className ="mb-3 col-md-2">
                    <select className="form-control" name='recipieIsVeg' onChange={handleChange} value={inputs.recipieIsVeg}>
                        <option value="veg">Veg</option>
                        <option value="nonveg">non-Veg</option>
                    </select>
                </div>
            </div>
            <div className="mb-3 col-md-12"> 
                <textarea 
                    className='form-control' 
                    type="text" 
                    name="recipieDescripton"
                    placeholder="Enter Recipie description"
                    value={inputs.recipieDescripton} 
                    onChange={handleChange}
                />
            </div>
            <div className='mb-3 col-md-3'>
                <label htmlFor='prevImage' >Upload preview image</label>
                <input value={selectedFile} onChange={(e) => setSelectedFile(e.target.files[0])} type="file" id="prevImage" />
            </div>
            <TagsInput inputs={inputs} setInputs={setInputs} />
            <button type="submit" className="btn btn-outline-dark">submit</button>
        </form>
        </div>
    );
}

export default AddNewRecipie;
