import React from 'react';
import { useParams } from "react-router-dom";
import { recipies } from './RecipieList';
import Step from './Step';

function RecipiePage() {
    const { recipieId } = useParams();

    const currentRecipie = recipies.filter(recipie => {
        console.log(recipie.id);
        return recipie.id == recipieId;
    })

    const Steps = currentRecipie[0].Steps;

    return (
        <div>
            <div>
                <img src={currentRecipie.imgSource} alt='recipieImage'/>
                <h1>{currentRecipie[0].title} </h1>
            </div>
            {Steps.map((step) => {
                return (<Step stepNumber={step.stepId} stepText={step.stepText} />)
            })}
        </div>
    )
}

export default RecipiePage;
