import React, {useState} from 'react';

function AddNewRecipie() {
    const [inputs, setInputs] = useState({
        recipieTitle: {
            type: String,
            required: [true, "No Name specified"]
        },
        recipieIngredients: [ingredientSchema],
        recipieAddedDate: Date,
        recipieDescripton: String,
        recipieIsVeg: {
            type: Boolean,
            required: [true, "specify if Veg or not"]
        },
        recipiePrevImage: imageSchema,
        recipiePrevDescription: String,
        recipieCalories: Number,
        recipieRating:{
            type: Number,
            min: 1,
            max: 5
        },
        recipieTags: [String],
        recpiesteps: [stepSchema]
    });

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
    }

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                name="username"
                placeholder='Enter Recipie name' 
                value={inputs.username || ""} 
                onChange={handleChange}
            />
            <textarea 
                type="number" 
                name="age"
                placeholder="Enter Recipie description"
                value={inputs.age || ""} 
                onChange={handleChange}
            />
            <input type="submit" />
        </form>
    );
}

export default AddNewRecipie;
