import React, {useState} from 'react';

function IngredientsInput(props) {
    const ingredientSchema = {
        ingredientId: undefined,
        ingredientName: "",
        ingredientAmount: undefined,
        ingredientUnit: ""
    };

    function addIngredientToList(element) {
        props.setInputs(values => ({...values, recipieIngredients: [...values.recipieTags]}));
    }
    
    function deleteIngredientFromList(id) {
        props.setInputs((prevValue) => {
            return prevValue.filter((value, index) => {
            return index !== id;
            });
        });
