import React, {useState} from 'react';

function AddNewRecipie() {
    const ingredientSchema = {
        ingredientId: Number,
        ingredientName: String,
        ingredientAmount: Number,
        ingredientUnit: String
    };

    const imageSchema = {
        imageName: String,
        path: String,
        size: Number
    };

    const stepSchema = {
        stepId: Number,
        stepTitle: String,
        stepDescription: String,
        stepImages: [imageSchema]
    };

    const [inputs, setInputs] = useState({
        recipieTitle: String,
        recipieIngredients: [ingredientSchema],
        recipieAddedDate: Date,
        recipieDescripton: String,
        recipieIsVeg: Boolean,
        recipiePrevImage: imageSchema,
        recipieCalories: Number,
        recipieTags: [String],
        recpiesteps: [stepSchema]
    });

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
    }

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                name="recipieTitle"
                placeholder='Enter Recipie name' 
                value={inputs.username || ""} 
                onChange={handleChange}
            />
            <select name='isVeg'>
                <option value="veg">Veg</option>
                <option value="nonveg">non-Veg</option>
            </select>
            <textarea 
                type="text" 
                name="recipieDescription"
                placeholder="Enter Recipie description"
                value={inputs.age || ""} 
                onChange={handleChange}
            />
            <input type="submit" />
        </form>
    );
}

export default AddNewRecipie;
