import React, {useEffect} from 'react';
import RecipieCard from './RecipieCard';
import axios from "axios";

const client = axios.create({
  baseURL: "http://localhost:5000" 
});

const recipies = [{
    id: 1,
    title:"Milk Tea",
    prevText:"Ready in 10 mins!",
    imgSource:"https://www.teaforturmeric.com/wp-content/uploads/2021/11/Masala-Chai-Tea-9-728x1092.jpg",
    Steps: [{
        stepId:1,
        stepText: "Boil water"
    },{
        stepId:2,
        stepText:"add tea"
    }]
},
{
    id: 2,
    title:"Hakka Noodles",
    prevText:"Easy chinese in lesss than 10 mins!",
    imgSource:"https://shwetainthekitchen.com/wp-content/uploads/2020/07/IMG_0100.jpg",
    Steps: [{
        stepId: 1,
        stepText: "Boil water"
    },{
        stepId: Number(2),
        stepText:"add noodles"
    }]
}];

function RecipieList() {
    useEffect(() => {
        client.get('/').then((response) => {
           const data = response.data;
           const recipies1 = data[0];
           console.log(data[1]);
        });
     }, []);

    return (
        <div className='row'>
            {recipies.map((currentRecipie) => {
                return (<RecipieCard id={currentRecipie.id} key={currentRecipie.id} title={currentRecipie.title} prevText={currentRecipie.prevText} imgSource={currentRecipie.imgSource}  />)
            })}
        </div>
    )
}

export default RecipieList;
export {recipies};
