import React, {useEffect, useState } from 'react';
import { useParams} from "react-router-dom";
import Step from './Step';
import axios from "axios";

const client = axios.create({
    baseURL: "http://localhost:5000" 
});

function RecipiePage() {
    const { recipieName } = useParams();

    const [recipie, setRecipie] = useState({});

    useEffect(() => {
        client.get('/recipies', { params: { title: recipieName } }).then((response) => {
            setRecipie(response.data);
        });
     }, []);

    console.log(recipie);

    return (
        <div>
            <div className='recipiePageTitle'>
                <div className='RecipiePageImage'>
                    <img src={recipie.recipiePrevImage.path} alt='recipieImage' style={{ height:"100%", width: "100%", objectFit: "contain" }}/>
                </div>
                <div className='recipiePageDesc'>
                    <h1>{recipie.recpieTitle} </h1>
                    <hr></hr>
                    <p>Recipie Description</p>
                </div>
            </div>
            <div className='RecipiePageSteps'>
                {recipie.recpiesteps.map((step) => {
                    return (<Step key={step.stepId} stepNumber={step.stepId} stepText={step.stepText} />)
                })}
            </div>
        </div>
    )
}

export default RecipiePage;
